import React, { useState } from 'react';
import axios from 'axios';
import { getUser, logout } from './service/AuthService'; // Assuming you have these functions

const homeAPIUrl = 'https://q3358v04o2.execute-api.us-east-1.amazonaws.com/prod/home';

const Home = () => {
  const currentUser = getUser(); // Get the current logged-in user from AuthService
  const [title, setTitle] = useState('');
  const [year, setYear] = useState('');
  const [artist, setArtist] = useState('');
  const [musicInfo, setMusicInfo] = useState('');
  const [errorMessage, setErrorMessage] = useState(null);

  const handleQuery = async (event) => {
    event.preventDefault();

    const requestBody = {
      username: currentUser.username, // Assuming AuthService provides the username of the logged-in user
      title: title.trim(),
      year: year.trim(),
      artist: artist.trim()
    };

    try {
      const response = await axios.post(homeAPIUrl, requestBody);
      setMusicInfo(response.data.message);
    } catch (error) {
      console.error('Error querying music:', error);
      setErrorMessage('Error querying music. Please try again later.');
    }
  };

  const handleLogout = () => {
    logout(); // Call the logout function from AuthService
    // Redirect to login page or handle logout action
  };

  return (
    <div>
      <h2>Welcome, {currentUser.username}!</h2>
      <form onSubmit={handleQuery}>
        <label>Title:</label>
        <input type="text" value={title} onChange={(event) => setTitle(event.target.value)} /><br />
        <label>Year:</label>
        <input type="text" value={year} onChange={(event) => setYear(event.target.value)} /><br />
        <label>Artist:</label>
        <input type="text" value={artist} onChange={(event) => setArtist(event.target.value)} /><br />
        <button type="submit">Query</button>
      </form>
      {musicInfo && (
        <div>
          <p>Music Information:</p>
          <p>{musicInfo}</p>
        </div>
      )}
      <button onClick={handleLogout}>Logout</button>
      {errorMessage && <p className="message">{errorMessage}</p>}
    </div>
  );
};

export default Home;
