import React, { useState, useEffect } from 'react';
import axios from 'axios';

const homeUrl = 'https://q3358v04o2.execute-api.us-east-1.amazonaws.com/prod/home'; // Replace with your actual API endpoint URL

const Home = () => {
  const [musicInfo, setMusicInfo] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    // Fetch subscribed music information when the component mounts
    fetchMusicInfo();
  }, []);

  const fetchMusicInfo = () => {
    axios.get(homeUrl)
      .then(response => {
        // Handle successful response
        setMusicInfo(response.data.music);
      })
      .catch(error => {
        // Handle error
        setErrorMessage('Failed to fetch music information');
      });
  };

  const handleRemove = (music) => {
    // Make a DELETE request to the backend API to remove the music
    axios.delete(`${homeUrl}/${music.id}`)
      .then(response => {
        // Handle successful removal
        // Update the UI to remove the deleted music from the list
        setMusicInfo(prevMusicInfo => prevMusicInfo.filter(item => item.id !== music.id));
      })
      .catch(error => {
        // Handle error
        console.error('Error removing music:', error);
        // Optionally, show an error message to the user
      });
  };

  return (
    <div>
      <h1>Subscribed Music</h1>
      {errorMessage && <p className="error">{errorMessage}</p>}
      <ul>
        {musicInfo.map((music, index) => (
          <li key={index}>
            <div>
              <p>Title: {music.title}</p>
              <p>Artist: {music.artist}</p>
              <p>Year: {music.year}</p>
            </div>
            <button onClick={() => handleRemove(music)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
