import React, { useState } from 'react';
import axios from 'axios';

const registerUrl = 'https://q3358v04o2.execute-api.us-east-1.amazonaws.com/prod/register';

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const submitHandler = async (event) => {
    event.preventDefault();

    if (!name.trim() || !email.trim() || !username.trim() || !password.trim()) {
      setMessage('All fields are required');
      return;
    }

    console.log('Submit button is pressed!');

    const requestBody = {
      username: username,
      email: email,
      name: name,
      password: password
    };

    const requestConfig = {
      headers: {
        'x-api-key': 'aWXFB88QHgaTNyCBVkrE91GbEUeeE8Jb50MoKz80'
      }
    };

    try {
      const response = await axios.post(registerUrl, requestBody, requestConfig);
      setMessage('Registration Successful');
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setMessage(error.response.data.message);
      } else {
        setMessage('An error occurred during registration');
      }
    }
  };

  return (
    <div>
      <form onSubmit={submitHandler}>
        <h1>Register</h1>
        Name: <input type="text" value={name} onChange={event => setName(event.target.value)} /><br />
        Email: <input type="text" value={email} onChange={event => setEmail(event.target.value)} /><br />
        Username: <input type="text" value={username} onChange={event => setUsername(event.target.value)} /><br />
        Password: <input type="password" value={password} onChange={event => setPassword(event.target.value)} /><br />
        <button type="submit">Register</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default Register;
