  const [year, setYear] = useState('');
  const [artist, setArtist] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const loginAPIUrl = 'https://q3358v04o2.execute-api.us-east-1.amazonaws.com/prod/login';
  const registerUrl = 'https://q3358v04o2.execute-api.us-east-1.amazonaws.com/prod/register';

  const handleLogin = (username, password) => {
    axios.post(loginAPIUrl, { username, password })
      .then(response => {
        setUserName(response.data.user);
        // Additional logic after successful login
      })
      .catch(error => {
        setErrorMessage('Login failed. Please try again.');
      });
  };

  const handleRegister = (name, email, username, password) => {
    axios.post(registerUrl, { name, email, username, password })
