import React, { useState, useEffect } from 'react';
import axios from 'axios';

const homeUrl = 'https://q3358v04o2.execute-api.us-east-1.amazonaws.com/prod/home'; // Replace with your actual API endpoint URL

const Home = () => {
  const [musicInfo, setMusicInfo] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [username, setUsername] = useState('');
  const [title, setTitle] = useState('');
  const [year, setYear] = useState('');
  const [artist, setArtist] = useState('');

  useEffect(() => {
    // Fetch subscribed music information when the component mounts
    fetchMusicInfo();
    // Fetch username when the component mounts
    fetchUsername();
  }, []);

  const fetchMusicInfo = () => {
    axios.get(homeUrl)
      .then(response => {
        // Handle successful response
        setMusicInfo(response.data.music);
      })
      .catch(error => {
        // Handle error
        setErrorMessage('Failed to fetch music information');
      });
  };

  const fetchUsername = () => {
    // Make a request to fetch the username from the backend
    // Assuming there's an API endpoint for fetching the username
    axios.get('https://your-backend-url.com/username')
      .then(response => {
        setUsername(response.data.username);
      })
      .catch(error => {
        console.error('Error fetching username:', error);
        // Optionally, set an error message or handle it in another way
      });
  };

  const handleQuery = () => {
    // Make a request to query music information based on the provided parameters
    axios.post(homeUrl, { title, year, artist })
      .then(response => {
        // Handle successful response
        setMusicInfo(response.data.musicInfo);
        if (response.data.musicInfo.length === 0) {
          setErrorMessage('No result is retrieved. Please query again.');
        } else {
          setErrorMessage('');
        }
      })
      .catch(error => {
        // Handle error
        setErrorMessage('Failed to query music information');
      });
  };

  return (
    <div>
      <h1>Welcome, {username}</h1>
      <div>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Year"
          value={year}
          onChange={(e) => setYear(e.target.value)}
        />
        <input
          type="text"
          placeholder="Artist"
          value={artist}
          onChange={(e) => setArtist(e.target.value)}
        />
        <button onClick={handleQuery}>Query</button>
      </div>
      {errorMessage && <p className="error">{errorMessage}</p>}
      <h2>Queried Music Information</h2>
      <ul>
        {musicInfo.map((music, index) => (
          <li key={index}>
            <div>
              <p>Title: {music.title}</p>
              <p>Artist: {music.artist}</p>
              <p>Year: {music.year}</p>
            </div>
          </li>
        ))}
      </ul>
      <button>Logout</button>
    </div>
  );
};

export default Home;
